import React, { useState, useEffect } from 'react';

import Mytable from '../Table';

const MyForm = () => {
    const [textInput, setTextInput] = useState('');
    const [dateInput, setDateInput] = useState('');
    const [checkboxInput, setCheckboxInput] = useState(false);
    const [tableData, setTableData] = useState([]);

    useEffect(() => {
        // 从本地存储中读取数据
        const storedData = localStorage.getItem('tableData');

        if (storedData) {
            setTableData(JSON.parse(storedData));
        }
    }, []);

    useEffect(() => {
        // 将数据存储到本地存储中
        localStorage.setItem('tableData', JSON.stringify(tableData));
    }, [tableData]);

    const handleTextChange = (event) => {
        setTextInput(event.target.value);
    };

    const handleDateChange = (event) => {
        setDateInput(event.target.value);
    };

    const handleCheckboxChange = (event) => {
        setCheckboxInput(event.target.checked);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        // 创建新的数据对象
        const newData = {
            text: textInput,
            date: dateInput,
            checkbox: checkboxInput,
            time: new Date().toLocaleTimeString() // Add the current time
        };

        // 将新的数据对象添加到表格数据中
        setTableData([...tableData, newData]);

        // 清空输入字段
        setTextInput('');
        setDateInput('');
        setCheckboxInput(false);
    };

    const handleClearTable = () => {
        // 清空表格数据
        setTableData([]);
    };

    return (
        <div className='grid justify-center'>
            <Mytable />
            <form onSubmit={handleSubmit} className='pt-10'>
                <label>
                    記錄時件:
                    <input className="border-2 rounded mr-5 ml-2" type="text" value={textInput} onChange={handleTextChange} />
                    日期:
                    <input type="date" value={dateInput} onChange={handleDateChange} />
                    進度:
                    <input
                        type="checkbox"
                        checked={checkboxInput}
                        onChange={handleCheckboxChange}
                    />
                </label>
                <div className='pt-10'>
                    <button className="group inline-flex items-center justify-center rounded-full py-2 px-4 text-sm font-semibold focus:outline-none focus-visible:outline-2 focus-visible:outline-offset-2 bg-slate-900 text-white hover:bg-slate-700 hover:text-slate-100 active:bg-slate-800 active:text-slate-300 focus-visible:outline-slate-900" type="submit">Add to Table</button>
                    <button type="button" className="group inline-flex items-center justify-center rounded-full py-2 px-4 text-sm font-semibold focus:outline-none focus-visible:outline-2 focus-visible:outline-offset-2 bg-slate-900 text-white hover:bg-slate-700 hover:text-slate-100 active:bg-slate-800 active:text-slate-300 focus-visible:outline-slate-900" onClick={handleClearTable}>清空表格</button>
                </div>
            </form>
            <br />
        </div>
    );
};

export default MyForm;
